import React from 'react';
import BoggleSolver from './Boggle Implementation/boggle_solver';
// import StartButton from './StartButton';
// import StopButton from './StopButton';
import { Grid, Button } from '@material-ui/core'
import './App.css';

const gridItems = {
  0: { size: 3, label: "A" },
  1: { size: 3, label: "B" },
  2: { size: 3, label: "C" },
  3: { size: 3, label: "D" },
}



class App extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      startState: false,
      answers: ' ',
    }
  }

  async componentDidMount() {
    // const response = await new StartButton();
    // console.log(response);
    // this.setState({ startState: response.state.state });
    console.log(BoggleSolver)
    this.setState({ answers: BoggleSolver})
  }

  render() {
    return (
      <div className="App">
        <header className="App-header">
        <p>Words Found</p>
        <p>{this,state.answers}</p>
          {/* <img src={logo} className="App-logo" alt="logo" /> */}

          {/* <a
            className="App-link"
            href="https://reactjs.org"
            target="_blank"
            rel="noopener noreferrer"
          >
            Learn React
          </a> */}


          {this.state.state === true && <Grid container justify="center" spacing={100}>
            {/* Row 1 */}
            <Grid container justify="center" item xs={12} spacing={3}>

              {Object.keys(gridItems).map((rowKey) => {
                return (
                  <Grid item key={rowKey} xs={gridItems[rowKey].size}>
                    {gridItems[rowKey].label}
                  </Grid>
                );
              })}
            </Grid>

            {/* Row 2 */}
            <Grid container justify="center" item xs={12} spacing={3}>
              {Object.keys(gridItems).map((rowKey) => {
                return (
                  <Grid item key={rowKey} xs={gridItems[rowKey].size}>
                    {gridItems[rowKey].label}
                  </Grid>
                );
              })}
            </Grid>

            {/* Row 3 */}
            <Grid container justify="center" item xs={12} spacing={3}>
              {Object.keys(gridItems).map((rowKey) => {
                return (
                  <Grid item key={rowKey} xs={gridItems[rowKey].size}>
                    {gridItems[rowKey].label}
                  </Grid>
                );
              })}
            </Grid>

            {/* Row 4 */}
            <Grid container justify="center" item xs={12} spacing={3}>
              {Object.keys(gridItems).map((rowKey) => {
                return (
                  <Grid item key={rowKey} xs={gridItems[rowKey].size}>
                    {gridItems[rowKey].label}
                  </Grid>
                );
              })}
            </Grid>
          </Grid>}


          {/* <StartButton promptText="Start" />
          <StopButton promptText="Stop" />  */}

          <Button variant="contained" color="primary" onClick={() => {
            console.log("Pressed");
            this.setState({ state: true });
            console.log(this.state.state);
          }}> Start </Button>

          <Button variant="contained" color="secondary" onClick={() => {
            console.log("Pressed");
            this.setState({ state: false });
            console.log(this.state.state);
          }}> Stop </Button>

        </header>
      </div>
    );
  }

}

export default App;
