import React from 'react';
import logo from './logo.svg';
// import StartButton from './StartButton';
import StopButton from './StopButton';
import { Grid, Button } from '@material-ui/core'
import './App.css';

const gridItems = {
  0: { size: 3, label: "A" },
  1: { size: 3, label: "B" },
  2: { size: 3, label: "C" },
  3: { size: 3, label: "D" },
}

class StartButton extends React.Component {

  constructor(props) {
      super(props);
      this.state = {
          startState: false,
      }
  }

  render() {
      return (
          <div>
              <Button variant="contained" color="primary" onClick={() => {
                  console.log("Pressed");
                  this.setState({ startState: true });
                  console.log(this.state.startState);
              }}> Start </Button>
          </div>
      )
  }

}

class App extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      startState: false,
    }
  }



  render() {
    return (
      <div className="App">
        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <p>
            Edit <code>src/App.js</code> and save to reload.
          </p>
          <a
            className="App-link"
            href="https://reactjs.org"
            target="_blank"
            rel="noopener noreferrer"
          >
            Learn React
          </a>


          {this.state.startState === true && <Grid container justify="center" spacing={100}>
            {/* Row 1 */}
            <Grid container justify="center" item xs={12} spacing={3}>

              {Object.keys(gridItems).map((rowKey) => {
                return (
                  <Grid item key={rowKey} xs={gridItems[rowKey].size}>
                    {gridItems[rowKey].label}
                  </Grid>
                );
              })}
            </Grid>

            {/* Row 2 */}
            <Grid container justify="center" item xs={12} spacing={3}>
              {Object.keys(gridItems).map((rowKey) => {
                return (
                  <Grid item key={rowKey} xs={gridItems[rowKey].size}>
                    {gridItems[rowKey].label}
                  </Grid>
                );
              })}
            </Grid>

            {/* Row 3 */}
            <Grid container justify="center" item xs={12} spacing={3}>
              {Object.keys(gridItems).map((rowKey) => {
                return (
                  <Grid item key={rowKey} xs={gridItems[rowKey].size}>
                    {gridItems[rowKey].label}
                  </Grid>
                );
              })}
            </Grid>

            {/* Row 4 */}
            <Grid container justify="center" item xs={12} spacing={3}>
              {Object.keys(gridItems).map((rowKey) => {
                return (
                  <Grid item key={rowKey} xs={gridItems[rowKey].size}>
                    {gridItems[rowKey].label}
                  </Grid>
                );
              })}
            </Grid>
          </Grid>}


          <StartButton  onClick={() => console.log("Outside")} promptText="Start" />
          <StopButton onClick={() => console.log("STOP")} promptText="Stop" />
        </header>
      </div>
    );
  }

}

export default App;
